        getLanguageFieldOptionByActivity: function (fieldKey) {
            var option = utils.getFieldOptionByActivity(fieldKey);
            if (option) {
                option = JSON.parse(JSON.stringify(option));

                function filterLang(opt) {
                    if (!opt || typeof(opt) != 'object') {
                        return false;
                    }

                    if (opt.type == 'array' || opt.type == 'map') {
                        var hasLang = false;
                        for (var k in opt.groupFieldOption) {
                            if (filterLang(opt.groupFieldOption[k])) {
                                hasLang = true;
                            } else if (!(opt.type == 'map' && opt.groupFieldOption[k].isMapKey)) {
                                delete opt.groupFieldOption[k];
                            }
                        }
                        if (hasLang) {
                            return true;
                        }
                    } else {
                        if (opt.isLanguage) {
                            return true;
                        }
                    }
                    return false;
                }

                var hasLang = false;
                for (var k in option) {
                    if (!filterLang(option[k])) {
                        delete option[k];
                    } else {
                        hasLang = true;
                    }
                }
                if (!hasLang) {
                    option = undefined;
                }
            }
            console.log(fieldKey, option);
            return option;
        },
        isNumber: function (text) {
            if (text == "") return false;
            
            if ($.isNumeric(text))
            {
                return true;
            }
            return false;
        },
    };

