                login_serial_config: {
                    name: '登陆序列号配置',
                    type: 'array',
                    vertical: true,
                    checkValid: function(data) {
                        //alert(JSON.stringify(data));
                        for (i in data) {
                            var version = data[i].version;
                            var serial = data[i].serial;

                            var versions = version.split(".");
                            if (versions.length != 2) {
                                return "不正确的版本号配置length" + version;
                            }

                            var version1 = versions[0];
                            var version2 = versions[1];

                            if (!$.isNumeric(version1)
                                || (!$.isNumeric(version2) && version2 != "*")){
                                return "不正确的版本号配置" + version;
                            }

                            if (null == serial || 'undefined' == serial || "" == serial) {
                                return "版本号" + version + "对应的序列号" + serial + "不正确";
                            }
                        }
                        
                        return true;
                    },
