                        //alert(JSON.stringify(data));
                        for (i in data) {
                            var country_list = data[i].country_list;
                            var lbs_rank_max_num_limit = data[i].lbs_rank_max_num_limit;
                            var lbs_rank_title_num_limit = data[i].lbs_rank_title_num_limit;
                            var lbs_rank_mmr_pct_limit = data[i].lbs_rank_mmr_pct_limit;

                            var countrys = country_list.split(";");
                            for (var j = 0; j < countrys.length; ++j)
                            {
                                var country = countrys[j];
                                if (country != "*" && country.length != 2)
                                {
                                    return "不正确的国家配置长度" + country;
                                }
                            }

                            var max_num = lbs_rank_max_num_limit.split(";");
                            var title_num = lbs_rank_title_num_limit.split(";");
                            var mmr_pct = lbs_rank_mmr_pct_limit.split(";");
                            if (max_num.length != title_num.length || mmr_pct.length != max_num.length)
                            {
                                return "称号人数数量和排行榜最大人数数量配置不一致";
                            }

                            for (var j = 0; j < max_num.length; ++j)
                            {
                                var max = parseInt(max_num[j]);
                                var title = parseInt(title_num[j]);
                                var pct = parseInt(mmr_pct[j]);

                                if (pct > 100)
                                {
                                    return "不能超过100" + pct;
                                }
                                
                                if (title > max)
                                {
                                    return "称号人数 " + title + " 不能超过最大人数" + max;
                                }
                            }
                        }
                        
